import Link from "next/link";

const PaintBloc = ({
  _id,
  name,
  details,
  width,
  height,
  date,
  smallImage,
  bigImage,
  photo,
  margin
}) => {
  console.log(margin);

  return (
    <Link href="/paint/[id]" as={`/paint/${name}`}>
      <div className="paint-container d-flex justify-center align-center">
        <img className="paint" {...photo} alt={name} />
        <div className="paint-name">{name.toUpperCase()}</div>
        <div className="paint-dimensions">{width}</div>
        <style jsx>
          {`
            .paint-container {
              position: relative;
              cursor: pointer;
              font-size: 2rem;
              margin: ${margin}px;
            }
            .paint {
              transition: 0.2s;
            }
            .paint-container:hover .paint {
              filter: contrast(1.2) brightness(0.5);
              transition: 0.2s;
            }
            .paint-name {
              color: white;
              font-weight: bold;
              position: absolute;
              border-radius: 5px;
              opacity: 0;
              transition: 0.2s;
            }
            .paint-dimensions {
              color: white;
              font-weight: bold;
              position: absolute;
              border-radius: 5px;
              opacity: 0;
              transition: 0.2s;
            }
            .paint-container:hover .paint-name,
            .paint-container:hover .paint-dimensions {
              opacity: 1;
              transition: 0.2s;
            }
          `}
        </style>
      </div>
    </Link>
  );
};

export default PaintBloc;
