import Link from "next/link";

const PaintBloc = ({
  _id,
  name,
  details,
  dimensions,
  date,
  smallImage,
  bigImage,
  isMiddleColumn
}) => {
  return (
    <>
      <Link href="/paint/[id]" as={`/paint/${name}`}>
        <div
          className={
            !isMiddleColumn
              ? "paint-container d-flex justify-center align-center"
              : "paint-container middle-column d-flex justify-center align-center"
          }
        >
          <img className="paint" src={smallImage} alt={name} />
          <div className="paint-infos">{name}</div>
        </div>
        <style jsx>
          {`
            .paint-container {
              width: calc((100% - 40px) / 3);
              height: calc((100% - 40px) / 3);
              margin-bottom: 20px;
              position: relative;
              cursor: pointer;
              font-size: 2rem;
            }
            .paint {
              width: 100%;
              transition: 0.2s;
            }
            .paint-container:hover .paint {
              filter: contrast(1.2) brightness(0.5);
              transition: 0.2s;
            }
            .middle-column {
              margin: 0 20px 20px 20px;
            }
            .paint-infos {
              color: white;
              font-weight: bold;
              position: absolute;
              padding: 10px;
              border-radius: 5px;
              opacity: 0;
              transition: 0.2s;
            }
            .paint-container:hover .paint-infos {
              opacity: 1;
              transition: 0.2s;
            }
          `}
        </style>
      </Link>
    </>
  );
};

export default PaintBloc;
