import Link from "next/link";

const PaintBloc = ({
  _id,
  name,
  details,
  width,
  height,
  date,
  smallImage,
  bigImage,
  photo,
  margin
}) => {
  console.log(margin);

  return (
    <Link href="/paint/[id]" as={`/paint/${name}`}>
      <div className="paint-container d-flex d-flex justify-center align-center">
        <img className="paint" {...photo} alt={name} />
        <div className="paint-infos d-flex flex-column align-center">
          <div className="paint-name">{name.toUpperCase()}</div>
          <div className="paint-dimensions">
            {width}cm x {height}cm
          </div>
        </div>
        <style jsx>
          {`
            .paint-container {
              cursor: pointer;
              margin: ${margin}px;
              position: relative;
            }
            .paint {
              transition: 0.2s;
            }
            .paint-container:hover .paint {
              filter: contrast(1.2) brightness(0.5);
              transition: 0.2s;
            }
            .paint-infos {
              position: absolute;
            }
            .paint-name {
              color: white;
              font-size: 2rem;

              font-weight: bold;
              opacity: 0;
              transition: 0.2s;
            }
            .paint-dimensions {
              color: white;
              font-size: 1.5rem;
              opacity: 0;
              transition: 0.2s;
            }
            .paint-container:hover .paint-name,
            .paint-container:hover .paint-dimensions {
              opacity: 1;
              transition: 0.2s;
            }
          `}
        </style>
      </div>
    </Link>
  );
};

export default PaintBloc;
