// SPACING
container = "1024px";
spaceBetweenSections = "30px";
spaceBetweenBlocks = "20px";

// COLOURS
mainColour = "#363A67";
mainColourLighter = "rgba(68, 73, 132, 0.8)";
secondColour = "#309AE9";
alternateColour = "#6E7293";
alternateColourLight = "#b0abca";
mainLightColour = "#E0E3FC";
mainBackground = "#F5F5F5";
mainBlack = "#302E2E";
mainGrey = "#D9D9D9";

// STYLING
radius = "10px";
miniRadius = "5px";
shadow = `3px 3px 10px 2px ${mainBlackLowOpacity}`;
miniShadow = `3px 3px 5px rgba(48, 46, 46, 0.1)`;
miniShadowBlue = `0px 0px 10px rgba(54, 58, 103, 0.5)`;
